mapper.select:
select a.*, b.name source_file_name, b.uuid source_file_uuid from adi_knowledge_base_item a left join adi_file b on a.source_file_id = b.id where a.is_deleted = false and a.kb_uuid = #{kbUuid}
and a.title like CONCAT('%', #{keyword}, '%')
order by create_time desc

select *
from adi_knowledge_base_item a
where uuid = #{uuid}

select count(1)
from adi_knowledge_base_item a
         inner join adi_knowledge_base b on a.kb_id = b.id
where a.is_deleted = false
  and a.uuid = #{uuid}
  and b.owner_id = #{userId}
